package com.example.guardiancare.data.apiimport com.example.guardiancare.data.models.LoginRequestimport com.example.guardiancare.data.models.LoginResponseimport com.example.guardiancare.data.models.ProfileResponseimport com.example.guardiancare.data.models.RegisterRequestimport com.example.guardiancare.data.models.RegisterResponseimport com.example.guardiancare.data.models.UpdateProfileRequestimport com.example.guardiancare.data.models.UpdateProfileResponseimport retrofit2.Responseimport retrofit2.http.Bodyimport retrofit2.http.GETimport retrofit2.http.Headerimport retrofit2.http.POSTimport retrofit2.http.PUTinterface AuthApiService {    @POST("/api/auth/register")    suspend fun register(@Body request: RegisterRequest): Response<RegisterResponse>    @POST("/api/auth/login")    suspend fun login(@Body request: LoginRequest): Response<LoginResponse>        @GET("/api/users/profile")    suspend fun getProfile(@Header("Authorization") token: String): Response<ProfileResponse>        @PUT("/api/auth/profile")    suspend fun updateProfile(        @Header("Authorization") token: String,        @Body request: UpdateProfileRequest    ): Response<UpdateProfileResponse>}